//Optimisation : boucler sur les sociallink
import React, { Component } from "react";
import PropTypes from 'prop-types';
import SocialLink from "./sociallink";
import Linkedin from './../../img/linkedin.svg';
import Instagram from './../../img/instagram.svg';
import GitHub from './../../img/github-square.svg';
import Codepen from './../../img/codepen.svg';
import Youtube from './../../img/youtube-square.svg';


export default class Footer extends Component{
    render(){
        let styles ={
            footer : {
                width : "100vw",
                backgroundColor: "rgb(0, 0, 0)",
                display: "flex",
                flexFlow: "row wrap",
                justifyContent: "space-around",
                alignItems: "center",
                color : "white",
            },
            contact : {
                minWidth : "33vw",
                maxWidth : "40vw",
            },
            social : {
                minWidth : "33vw",
                maxWidth : "40vw",
                height : "4rem",
                display: "flex",
                flexFlow: "row nowrap",
                justifyContent: "space-around",
                alignItems: "center",
            },
        }
        return(
            <footer style={styles.footer}>
                <div>
                    <h3>{this.props.language==="fr"?"Me contacter :":"Contact :"}</h3>
                    <ul>
                        <li>desartadrien1@gmail.com</li>
                        <li>(+32) 471/48.98.74</li>
                    </ul>
                </div>
                <div style={styles.social}>
                    <SocialLink
                        href = "https://www.linkedin.com/in/adrien-desart"
                        img = {{
                            src : Linkedin,
                            alt : "Linkedin icon",
                        }}
                    />
                    <SocialLink
                        href = "https://www.instagram.com/desartadrien"
                        img = {{
                            src : Instagram,
                            alt : "Instagram icon",
                        }}
                    />
                    <SocialLink
                        href = "https://github.com/adriendesart"
                        img = {{
                            src : GitHub,
                            alt : "GitHub icon",
                        }}
                    />
                    <SocialLink
                        href = "https://codepen.io/adriendesart/"
                        img = {{
                            src : Codepen,
                            alt : "Codepen icon",
                        }}
                    />
                    <SocialLink
                        href = "https://www.youtube.com/channel/UCSI-b9dcEVarKndRCbtBg9w"
                        img = {{
                            src : Youtube,
                            alt : "Youtube icon",
                        }}
                    />
                </div>
                <span>&copy; Adrien Desart - 2020</span>
            </footer>
        )
    }
}

Footer.propTypes ={
    language : PropTypes.string,
}
