export const getBrowserLanguage =()=>{
    let lang = navigator.l
    let language = "";
    if(navigator.language){
        if(navigator.language.includes("fr")){
            language = "fr";
        } else {
            language = "en";
        }
    } else if(navigator.browserLanguage){
        if(navigator.browserLanguage.includes("fr")){
            language = "fr";
        } else {
            language = "en";
        }
    }
    return language;
}
