// To do : hover state animation
import React, { Component} from 'react';
import PropTypes from 'prop-types';
import {isVisible} from '../../utils/displayed';

export default class NavItem extends Component{
    state = {
        visible : false,
        hover : false,
    }
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll)
    }
    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll)
    }
    handleScroll = () =>{
        this.setState({
            visible: isVisible(this.props.navPoint) !== "unvisible"?true:false,
        })
    }
    render(){
        //css :
        let styles = {
            navItem : {
                position : "relative",
                left: "-100%",
            },
            navLink : {
                display : "flex",
                flexFlow: "row nowrap",
                justifyContent: "flex-end",
                alignItems: "center",
                margin: "1rem 0",
                backgroundColor: "black",
                borderRadius: "0 2.4rem 2.4rem 0",
                textDecoration: "none",
                color: "white",
                position: "relative",
                left: this.state.visible || this.state.hover
                ?"100%":"2.6rem",
                transition: "ease-in-out 0.75s" ,
            },
            subject :{
                padding: "0.2rem 1rem",
                textAlign: "right",
            },
            icon :{
                width: "2rem",
                height: "2rem",
                padding: "0.2rem",
                border: this.state.visible || this.state.hover?"1px solid rgb(145, 231, 16)":"1px solid white",
                borderRadius: "50%",
            },
        }

        return(
            <div style={styles.navItem}>
                <a style={styles.navLink} href={"#" + this.props.anchor}>
                    <span style={styles.subject}>{this.props.language==="en"?this.props.name.en:this.props.name.fr}</span>
                    <img style={styles.icon} src={this.props.icon.src} alt={this.props.icon.alt}/>
                </a>
            </div>
        )
    }
}

NavItem.propTypes = {
    anchor : PropTypes.string,
    name : PropTypes.object,
    icon : PropTypes.object,
    language : PropTypes.string,
    deviceType : PropTypes.string,
    deviceOrientation : PropTypes.string,
}
