import React, { Component } from "react";
import PropTypes from 'prop-types';

export default class Card extends Component{
    state = {
        over : false,
    }
    handleOver =()=>{
        this.setState({
            over: !this.state.over,
        })
    }
    render(){
        //css :
        let styles = {
            card :{
                position : "relative",
                backgroundImage : `url(${this.props.background})`,
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                width : this.props.deviceType === "Mobile"?"80vw":"20vw",
                height: "80vh",
                overflow : "hidden",
                border: "2px solid black",
                borderRadius : "1rem",
                display: "flex",
                flexFlow:"column nowrap",
                justifyContent: "flex-end",
                alignItems: "center",
            },
            movingSection :{
                position: "relative",
                width:"100%",
                backgroundColor:"white",
                transition: "transform 2s",
                overflow : "hidden",
                transform: this.state.over && "translateY(-100%)",
            },
            title :{
                width : "100%",
                padding : "0.2rem",
                textAlign : "center",
                backgroundColor:"white",
                margin : "0px",
                position : "relative",
            },
            description :{
                position : "absolute",
                top : "100%",
                left : "0",
                width : "100%",
                padding : "0.2rem",
                textAlign : "justify",
                backgroundColor:"white",
                margin : "0px",
            },
            footer : {
                position: "relative",
                width:"100%",
                backgroundColor:"#FFF",
                display:"flex",
                flexFlow:"row nowrap",
                justifyContent:"space-around",
                alignItems:"center",
            }
        }

        return(
            <div style={styles.card} onMouseEnter={this.handleOver} onMouseLeave={this.handleOver}>
                <section style={styles.movingSection}>
                    <h2 style={styles.title}>{this.props.language === "fr"?this.props.title.fr:this.props.title.en}</h2>
                    <p style={styles.description}>{this.props.language === "fr"?this.props.description.fr:this.props.description.en}</p>
                </section>
                <section>
                    <button></button>
                </section>
            </div>
        )
    }
}

Card.propTypes = {
    language : PropTypes.string,
    deviceType : PropTypes.string,
    deviceOrientation : PropTypes.string,
    title : PropTypes.string,
    description : PropTypes.string,
    background : PropTypes.string,
}
