import React, { Component} from 'react';
import PropTypes from 'prop-types';
import {isVisible} from './../utils/displayed';

export default class NavItem extends Component{
    state = {
        visible : false,
        styles : this.styles,
    };
    styles = {
        navItem : {
            position : "relative",
            left: "-100%",
        },
        navLink : {
            display : "flex",
            flexFlow: "row nowrap",
            justifyContent: "flex-end",
            alignItems: "center",
            margin: "1rem 0",
            backgroundColor: "black",
            borderRadius: "0 2.4rem 2.4rem 0",
            textDecoration: "none",
            color: "white",
            position: "relative",
            left: this.state.visible?"100%":"2.6rem",
            transition: "ease-in-out 0.75s" ,
        },
        subject :{
            padding: "0.2rem 1rem",
            textAlign: "right",
        },
        icon :{
            width: "2rem",
            height: "2rem",
            padding: "0.2rem",
            border: "1px solid white",
            borderRadius: "50%",
        },
    }
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
    };
    componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
    };
    handleScroll = () =>{
        this.setState({
            visible: isVisible(this.props.ancre) !== "unvisible"?true:false,
        });
    };

    render(){
        return(
            <div style={this.state.styles.navItem}>
                <a style={this.state.styles.navLink} href={"#" + this.props.ancre}>
                    <span style={this.state.styles.subject}>{this.props.language==="en"?this.props.name.en:this.props.name.fr}</span>
                    <img style={this.state.styles.icon} src={this.props.icon.src} alt={this.props.icon.alt}/>
                </a>
            </div>
        )
    }
}

NavItem.propTypes = {
    ancre : PropTypes.string,
    language : PropTypes.string,
    name : PropTypes.object,
    icon : PropTypes.object,
}
