import React, { Component, Fragment } from 'react';
import PropTypes from 'prop-types';
import NavItem from './navitem';
import FRFLAG from './../../img/Flag_of_France.svg';
import ENFLAG from './../../img/Flag_of_the_United_Kingdom.svg';
import LANGUAGE from './../../img/language.svg';

export default class Navbar extends Component{
    handleChangeLang = (e,newLang) =>{
        e.preventDefault()
        this.props.setLanguage(newLang)
    }

    render(){
        //css :
        let styles = {
            navBar : {
                height: "100vh",
                margin: "0",
                padding: "0",
                display: "flex",
                flexFlow: "column nowrap",
                justifyContent: "center",
                alignItems: "flex-end",
                position: "fixed",
                top: "0",
                left: "0",
                zIndex: "10",
            }
        }
        let navList = Object.keys(this.props.elements).map(item => (
            <NavItem 
                anchor={this.props.elements[item].anchor}
                navPoint={item}
                name={this.props.elements[item].name}
                icon={this.props.elements[item].icon}
                language={this.props.language}
                deviceType={this.props.deviceType}
                deviceOrientation={this.props.deviceOrientation}
            />
        ))
        return(
            <Fragment>
            <nav style={styles.navBar}>
                {navList}
            </nav>
            {/* <div id="language-selector">
                <div>
                    <button type="button" onClick={(e)=>this.handleChangeLang(e,"fr")}><img src={FRFLAG} alt="French flag"/></button>
                    <button type="button" onClick={(e)=>this.handleChangeLang(e,"en")}><img src={ENFLAG} alt="English flag"/></button>
                </div>
            </div> */}
            <div style={styles.navItem}>
                <div style={styles.navLink} onMouseEnter={this.handleOver} onMouseLeave={this.handleOver}>
                    <div>
                    <button type="button" onClick={(e)=>this.handleChangeLang(e,"fr")}>
                    <img src={FRFLAG} alt="French flag"/></button>
                    <button type="button" onClick={(e)=>this.handleChangeLang(e,"en")}><img src={ENFLAG} alt="English flag"/></button>
                    </div>
                    <img src={LANGUAGE} alt="language icon"/>
                </div>
            </div>
            </Fragment>
        )
    }
}

Navbar.propTypes = {
    elements : PropTypes.object.isRequired,
    language : PropTypes.string,
    setLanguage : PropTypes.func,
    deviceType : PropTypes.string,
    deviceOrientation : PropTypes.string,
}
