import React, { Component, Fragment } from 'react';
import Navbar from '../components/navigation/navbar';
import Parallax from '../components/welcome/parallax'
import './../css/welcome.css'
//image :
import ADBrand from './../img/ADbrand.svg';
import Male from './../img/male.svg';
import PaperPlane from './../img/paper-plane.svg';
import ArrowDown from './../img/arrow-down.svg';
import PortraitAD from './../img/PortraitAD.jpg';
import IntroBackground from '../img/DSC_0129.JPG';
import BlogBackground from '../img/DSC_0114.JPG';


export default class Welcome extends Component{
    //use nav Item with <span style={styles.navigationPoint} id="anchor"/> in div to use the anchor and navigation animation
    navbarItem = {
        item1 : {
            anchor :"intro",
            name:{
                en:"Home",
                fr:"Accueil",
            },
            icon:{
                src:ADBrand,
                alt:"AD icon",
            },
        },
        item2 : {
            anchor :"about",
            name:{
                en:"About me",
                fr:"A mon sujet",
            },
            icon:{
                src:Male,
                alt:"about icon",
            },
        },
        item3 : {
            anchor :"blogs",
            name:{
                en:"Blogs",
                fr:"Mes blogs",
            },
            icon:{
                src:PaperPlane,
                alt:"blog icon",
            },
        },
    }

    render(){
        //css :
        let styles = {
            navigationPoint:{
                position : "absolute",
                top: "50%",
                left: "50%",
            },
            backBrand :{
                width: "50vh",
                height: "50vh",
                backgroundColor: "rgb(0, 0, 0)",   
                border: "solid 1px rgb(0, 0, 0)",
                borderRadius: "50%",
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                marginTop: "3rem",
            },
            logoBrand :{
                width: "75%",
            },
            arrowBtn :{
                backgroundColor: "transparent",
                border: "none",
                color: "white",
                textDecoration: "none",
                fontSize: "2rem",
                display: "flex",
                flexFlow: "column nowrap",
                justifyContent: "space-around",
                alignItems: "center",
            },
            arrow :{
                height: "2rem",
                transform: "translateY(0)",
                animationName: "hvr-wobble-vertical",
                animationDuration: "1s",
                animationTimingFunction: "ease-in-out",
                animationIterationCount: "infinite",
            },
        }
        let wobble = `@keyframes hvr-wobble-vertical {
                16.65% {
                  transform: translateY(8px);
                }

                33.3% {
                  transform: translateY(-6px);
                }

                49.95% {
                  transform: translateY(4px);
                }

                66.6% {
                  transform: translateY(-2px);
                }

                83.25% {
                  transform: translateY(1px);
                }

                100% {
                  transform: translateY(0);
                }
            }`;

        return(
            <Fragment>
                <Navbar
                    elements={this.navbarItem}
                    language={this.props.language}
                    setLanguage={this.props.setLanguage}
                    deviceType={this.props.deviceType}
                    deviceOrientation={this.props.deviceOrientation}
                />
                <Parallax background={IntroBackground}>
                    <span style={styles.navigationPoint} id="intro"/>
                    <div style={styles.backBrand}>
                        <img src={ADBrand} alt="AD logo" style={styles.logoBrand}/>
                    </div>
                    <button disabled style={styles.arrowBtn}>
                        {this.props.language==="en"?"Welcome":"Bienvenu"}
                        <style children={wobble} />
                        <img src={ArrowDown} alt="scroll-down" style={styles.arrow}/>
                    </button>
                </Parallax>
                <section id="presentation">
                    <span style={styles.navigationPoint} id="about"/>
                    {(this.props.scrollPos === null || this.props.scrollPos === 0) && <span id="copyrightAD">&copy; Adrien Desart - 2020</span>}
                    <div className="block" id="test-2">
                        <img id="profil-picture" src={PortraitAD} alt="Adrien Desart portrait"/>
                        <div>
                            <blockquote>{this.props.language==="en"?"I'm Adrien. I work as commercial diver for the Puclic Service of Wallonia and also as freelance web developer. My passions are scuba diving, streetfishing, wed developpement and take pictures. This site would share about my work, my ideas and my project. I hope you will enjoy."
                            :"Bonjour, je m'apelle Adrien. Je suis contrôleur d'infrastructure subaquatique pour le compte du Service Publique de Wallonie ainsi que web développeur indépendant. Mes centres d'intérets sont la plongée, la pêche, le développement web et la photo. Ce site à pour vocation de partager avec ceux qui le désire mes passions, mes idées et mes travaux."}</blockquote>
                        </div>
                    </div>
                </section>
                <Parallax background={BlogBackground}>
                    <span style={styles.navigationPoint} id="blogs"/>
                </Parallax>
            </Fragment>
        )
    }
}
