import React, { Component } from "react";
import PropTypes from 'prop-types';

export default class Card extends Component{
    state = {
        over : false,
    }
    handleOver =()=>{
        this.setState({
            over: !this.state.over,
        })
    }
    render(){
        //css :
        let styles = {
            card :{
                position : "relative",
                backgroundImage : `url(${this.props.background})`,
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                width : this.props.deviceType === "Mobile"?"80vw":"20vw",
                height: "80vh",
                overflow : "hidden",
                border: "2px solid black",
                borderRadius : "1rem",
                display: "flex",
                flexFlow:"column nowrap",
                justifyContent: "flex-end",
                alignItems: "center",
            },
            movingSection :{
                position: "relative",
                width:"100%",
                transition: "transform 2s",
                transform: this.state.over && "translateY(-40vh)",
                opacity : "0.9",
            },
            hiddenSection :{
                overflow : "hidden",
                width:"100%",
                padding : "0",
                margin : "0",
            },
            title :{
                width : "100%",
                padding : "0.2rem",
                margin : "0",
                textAlign : "center",
                backgroundColor:"white",
                position : "relative",
                borderTop:"1px solid black",
                textDecoration:"underline",
            },
            description :{
                position : "absolute",
                top : "98%",
                left : "0",
                width : "100%",
                height : "40vh",
                padding : "0.2rem",
                margin : "0",
                textAlign : "justify",
                backgroundColor:"white",
            },
            footer : {
                position: "relative",
                width:"100%",
                backgroundColor:"#FFF",
                display:"flex",
                flexFlow:"row nowrap",
                justifyContent:"space-around",
                alignItems:"center",
                borderTop:"1px solid black",
            }
        }

        return(
            <div style={styles.card} onMouseEnter={this.handleOver} onMouseLeave={this.handleOver}>
                <section style={styles.movingSection}>
                    <dir style={styles.hiddenSection}>
                        <h2 style={styles.title}>{this.props.language === "fr"?this.props.title.fr:this.props.title.en}</h2>
                        <p style={styles.description}>{this.props.language === "fr"?this.props.description.fr:this.props.description.en}</p>
                    </dir>
                </section>
                <section style={styles.footer}>
                    <button>{this.props.language === "fr"?"Voir plus":"See more"}</button>
                </section>
            </div>
        )
    }
}

Card.propTypes = {
    language : PropTypes.string,
    deviceType : PropTypes.string,
    deviceOrientation : PropTypes.string,
    title : PropTypes.string,
    description : PropTypes.string,
    background : PropTypes.string,
}
