import React, { Component } from "react";
import PropTypes from 'prop-types';

export default class Card extends Component{
    render(){
        //css :
        let styles = {
            card :{
                backgroundImage : `url(${this.props.background})`,
                backgroundPosition: "center",
                backgroundRepeat: "no-repeat",
                backgroundSize: "cover",
                // width : this.props.deviceType === "Mobile"?"80vw":"20vw",
                height: "80vh",
            }
        }

        return(
            <div style={styles.card}>
                <section>
                    <h2>{this.props.language === "fr"?this.props.title.fr:this.props.title.en}</h2>
                    <p>{this.props.language === "fr"?this.props.description.fr:this.props.description.en}</p>
                </section>
                <button></button>
            </div>
        )
    }
}

Card.propTypes = {
    language : PropTypes.string,
    deviceType : PropTypes.string,
    deviceOrientation : PropTypes.string,
    title : PropTypes.string,
    description : PropTypes.string,
    background : PropTypes.string,
}
